import React, {useState, useEffect} from 'react';
import Logo from './Logo'
import {Link, Navigate} from "react-router-dom"; 
import AnimeSearchPage from '../AnimeSearchPage';
import { useNavigate } from 'react-router-dom';

const Navbar = ({refresh, setRefresh}) => {

  let navigate = useNavigate();

  const [search, setSearch] = useState();


  const HandleChange = (e) =>
  {
    setSearch(e.target.value);
  }

  const HandleSearch = (e) =>
  {
    e.preventDefault();
    navigate(`/search/${search}`);
    setRefresh(!refresh);
  }

  
  return (
    <div className='navbar'>
        <Logo/>
        <div className="navbar-search-container">
          <input type="text" className="navbar-search" placeholder="Search for an anime..." onChange={HandleChange}/>
          <button className="navbar-search-btn" type="submit" onClick={()=>{HandleSearch()}}>
            <i className="fas fa-search"></i>

          </button>
        </div>

    </div>
  )
}

export default Navbar
