import React, {useEffect, useState} from 'react';
import axios from "axios";
import delayAdapterEnhancer from 'axios-delay';

const Cast = ({id}) => {

    let url = `https://api.jikan.moe/v3/anime/${id}/characters_staff`;

    const [cast, setCast] = useState([]);
    const [actors, setActors] = useState([]);


    const api = axios.create({
        adapter: delayAdapterEnhancer(axios.defaults.adapter)
      });

      let cancelToken;

    useEffect(()=>{
        (async function()
        {
            try
            {
                if (typeof cancelToken != typeof undefined) cancelToken.cancel("Canceling previous req");
                cancelToken = axios.CancelToken.source();

                const res = await api.get(url, {cancelToken: cancelToken.token, delay: 8000});
                setCast(res.data.characters.filter(function(a){ return a.role === "Main" }).sort(function(a, b){return a.mal_id-b.mal_id}).concat(res.data.characters.filter(function(a){ return a.role === "Supporting" }).sort(function(a, b){return a.mal_id-b.mal_id})));
                setActors(res.data.characters.map((character)=>character.voice_actors.filter((actor)=>{return actor.language === "Japanese"})));

            }
            catch(err)
            {
                console.log(err);
            }
        })();
    }, [url]);

    return (
        <section className="container key-info-container cast-container">
            <h2 className='section-title'>Main Cast</h2>
            <div className='cast key-info-container'>
                {cast.map((character, i)=>
                <div key={i} className='character-row'>
                    <div className="character-box">
                        <div className='character-name'><strong>{character.name}</strong></div>
                        <div className='character-pic'>{character.image_url === "https://cdn.myanimelist.net/images/questionmark_23.gif?s=f7dcbc4a4603d18356d3dfef8abd655c" ? <i className="fas fa-question"></i> : <img src={character.image_url} alt="character-pic"></img> }</div>
                    </div>

                    {character.voice_actors.length > 0 ?
                        (character.voice_actors.map((actor, i)=>actor.language.includes("Japanese") ?

                            <div className="actor-box">
                                <div key={i} className='actor-name'>{actor.name ? actors[i].name : "Unknown"}</div>
                                <div className='actor-pic'><img src={actor.image_url} alt="actor-pic"></img></div>
                            </div> : (null)))
                    :
                    
                        (<div className="actor-box">
                            <div className='actor-name'>Unknown</div>
                            <div className='actor-pic'><i className="fas fa-question"></i></div>
                        </div>)}

                        
                </div>)}
                
            </div>
        </section>

    )
}

export default Cast;
