import React, {useEffect, useState} from 'react';
import { useParams } from 'react-router';
import axios from "axios";
import delayAdapterEnhancer from 'axios-delay';

const Footer = ({id}) => {
    let url = `https://api.jikan.moe/v4/anime/${id}/reviews`;

    const api = axios.create({
        adapter: delayAdapterEnhancer(axios.defaults.adapter)
      });

    let cancelToken;

    useEffect(()=>{
        (async function()
        {
            try
            {
                if (typeof cancelToken != typeof undefined) cancelToken.cancel("Canceling previous req");
                cancelToken = axios.CancelToken.source();
                const res = await api.get(url, {cancelToken: cancelToken.token, delay: 10});

                console.log(res.data);
            }
            catch(err)
            {
                console.log(err);
            }
        })();
    }, [url]);
  return (
    <footer>
        <div className="container">
            <div className="back-to-top">
                <a href="#"><i className="fas fa-chevron-up"></i></a>
            </div>
            <div className="footer-content">
                <div className="footer-content-divider animate-bottom">
                    <div className="meow">
                        <h4>Follow along</h4>
                        <ul className="social-icons">
                            <li>
                                <a href="#"><i className="fab fa-twitter"></i></a>
                            </li>
                            <li>
                                <a href="#"><i className="fab fa-instagram"></i></a>
                            </li>
                            <li>
                                <a href="#"><i className="fab fa-facebook-square"></i></a>
                            </li>
                        </ul>
                    </div>
                    <div className="newsletter-container">
                        <h4>Newsletter</h4>
                        <form className="newsletter-form">
                            <input type="text" className="newsletter-input" placeholder="Your email address..."/>
                            <button className="newsletter-btn" type="submit">
                                <i className="fas fa-envelope"></i>
                            </button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    </footer>
  )
}

export default Footer
