import React, {useEffect, useState} from 'react';
import { useParams } from 'react-router';
import axios from "axios";
import delayAdapterEnhancer from 'axios-delay';
import ReviewContent from './ReviewContent';
import { Link } from 'react-router-dom';

const ReviewList = ({episodeNumReviewPage}) => {

    const { id } = useParams();

    let url = `https://api.jikan.moe/v3/anime/${id}/reviews?q=&order_by=start_date&sort=desc&page=1`;

    const [reviews, setReviews] = useState([]);

    const api = axios.create({
        adapter: delayAdapterEnhancer(axios.defaults.adapter)
      });

      let cancelToken;

    useEffect(()=>{
        (async function()
        {
            try
            {
                if (typeof cancelToken != typeof undefined) cancelToken.cancel("Canceling previous req");
                cancelToken = axios.CancelToken.source();

                const res = await api.get(url, {cancelToken: cancelToken.token, delay: 0});
                setReviews(res.data.reviews);
                console.log(res.data.reviews);
            }
            catch(err)
            {
                console.log(err);
            }
        })();
    }, [url]);

  return (
    <div className="container key-info-container">
        <h2 className='section-title'>User Reviews</h2>
        <div className='reviews-box'>
            {reviews.map((review, i)=>
            <div key={i} className='review'>
                <div className="review-row">
                    <div className="user-profile">
                        <img src={review.reviewer.image_url} alt="Reviewer pic" />
                        <h3 className='username'>{review.reviewer.username}</h3>
                        <p>{[new Date(review.date).toString().slice(4, 10), ", ", review.date.toString().slice(0, 4)].join("")}</p>
                    </div>
                    <div className="score-and-seen">
                        <div className="review-score"><span><span id='score-big'><i className='fas fa-star'></i>{review.reviewer.scores.overall}</span> / 10</span></div>
                        <div className="review-seen"><span><span id='score-big'><i className='fas fa-eye'></i>{review.reviewer.episodes_seen}</span> / {episodeNumReviewPage}</span></div>
                    </div>
                </div>

                <ReviewContent review={review}/>

                <div className="review-helpful"><span><i className='fas fa-thumbs-up'></i> Helpful <div className='circle'></div>{review.helpful_count}</span></div>

            </div>)}
            
        </div>
    </div>
  )
}

export default ReviewList
