import React, {useState, useEffect} from 'react';
import axios from "axios";
import Anime from './Anime';


const Home = () => {

    const [url, setUrl] = useState("https://api.jikan.moe/v3/search/anime?q=&order_by=score&sort=desc&page=1");
    const [animes, setAnimes] = useState([]);
    const [search, setSearch] = useState("");

    const HandleSearch = (event) =>
    {
        setSearch(event.target.value)
    }

    const HandleSort = (event) =>
    {
        setUrl(`https://api.jikan.moe/v3/search/anime?q=&order_by=${event.target.value}&sort=desc&page=1`);
        console.log(event.target.value);
    }

    const filterdAnime = animes.filter(anime=>{
        return anime.title.toLowerCase().includes(search.toLowerCase());
    })

    useEffect(() =>
    {
        (async function()
        {
            try
            {
                const res = await axios.get(url);
                setAnimes(res.data.results);
                console.log(res.data);
            }
            catch(err)
            {
                console.log(err);
            }
        })();

    }, [url])

    return (
        <div>
            <nav>
                <input type="text" className="search-box" placeholder='Search for an anime...' value={search} onChange={HandleSearch}/>
                <ul>
                    <li><button onSubmit={HandleSubmit}>Action</button></li>
                    <li><button onSubmit={HandleSubmit}>Adventure</button></li>
                    <li><button onSubmit={HandleSubmit}>Drama</button></li>
                    <li><button onSubmit={HandleSubmit}>Sci-fi</button></li>
                    <li><button onSubmit={HandleSubmit}>Comedy</button></li>
                    <li><button onSubmit={HandleSubmit}>Romance</button></li>
                </ul>
            </nav>
            
            
            <select id="sort" onChange={HandleSort}>
                <option value="score">Ranking</option>
                <option value="members">Popularity</option>
                <option value="start_date">Newest</option>
                <option value="end_date">Oldest</option>
            </select>

            <div className='anime-list'>
                {filterdAnime.map((anime, i)=>{return <Anime key={i} id={anime.mal_id} title={anime.title}/>})}
            </div>

        </div>

    )
}

export default Home;
