import axios from 'axios';
import delayAdapterEnhancer from 'axios-delay';
import React, { useEffect, useState, useRef } from 'react';
import ReactPlayer from "react-player/youtube";

import {useSwipeable} from "react-swipeable";

const Videos = ({id}) => {

    let url = `https://api.jikan.moe/v3/anime/${id}/videos`;

    const [videos, setVideos] = useState([]);
    const [style, setStyle] = useState(0);
    const [clicks, setClicks] = useState(0);

    const widthRef = useRef();
    const videoWidthRef = useRef();


    const slideRight = function()
    {
        
        if (Math.abs(widthRef.current.getBoundingClientRect().right) > videoWidthRef.current.getBoundingClientRect().width)
        {
            setStyle(style + 242);
            setClicks(clicks + 1);
            console.log(widthRef.current.getBoundingClientRect());
            console.log(videoWidthRef.current.getBoundingClientRect());
        }
    }

    const slideLeft = function()
    {
        
        if (widthRef.current.getBoundingClientRect().left < videoWidthRef.current.getBoundingClientRect().left)
        {
            setStyle(style - 242);
            setClicks(clicks - 1);
            console.log(widthRef.current.getBoundingClientRect());
        }
    }

    const handlers = useSwipeable({
        onSwipedLeft: () => slideRight(),
        onSwipedRight: () => slideLeft()
    });

    const api = axios.create({
        adapter: delayAdapterEnhancer(axios.defaults.adapter)
      });

      let cancelToken;

    useEffect(()=>{
        (async function()
        {
            try
            {
                if (typeof cancelToken != typeof undefined) cancelToken.cancel("Canceling previous req");
                cancelToken = axios.CancelToken.source();

                const res = await api.get(url, {cancelToken: cancelToken.token, delay: 4000});
                setVideos(res.data.promo);
                console.log(res.data);
                console.log(widthRef.current.getBoundingClientRect())
                console.log(videoWidthRef.current.getBoundingClientRect())


            }
            catch(err)
            {
                console.log(err);
            }
        })()
    }, [url]);

    return (
        (videos) ?
        (
            <section className='container key-info-container videos-container'>
                <h2>Videos</h2>

                <div className="arrow-container">
                    <i class="fas fa-arrow-alt-square-left" onClick={slideLeft}></i>
                        <div {...handlers} ref={videoWidthRef} className="video-slider-container">
                            <div ref={widthRef} className="video-slider" style={{transform: 'translateX(-' + style + 'px)'}}>
                                
                                {videos.map((video, i)=>
                                    <div key={i} className='video'>
                                        <ReactPlayer className='player' title={`video ${i}`} width="100%" height="100%" controls="true" muted="true" url={`${video.video_url}`}/>
                                    </div>)}
                            </div>
                        </div>
                    <i class="fas fa-arrow-alt-square-right" onClick={slideRight}></i>
                </div>
                

            </section>
        ) : (null)

    )
}

export default Videos;
