import axios from 'axios';
import delayAdapterEnhancer from 'axios-delay';
import React, { useEffect, useState, useRef } from 'react';
import ReactPlayer from "react-player/youtube";
import Carousel, {consts} from 'react-elastic-carousel';
import {useSwipeable} from "react-swipeable";

const Videos = ({id}) => {

    let url = `https://api.jikan.moe/v3/anime/${id}/videos`;

    const [videos, setVideos] = useState([]);
    const [style, setStyle] = useState(0);
    const [clicks, setClicks] = useState(0);

    const breakPoints = 
    [
        { width: 1, itemsToShow: 1},
        { width: 630, itemsToShow: 2},
        { width: 980, itemsToShow: 3 }
    ]

    const myArrow = function({ type, onClick, isEdge }) 
    {
        const pointer = type === consts.PREV ? <i class="fas fa-arrow-alt-square-left"></i> : <i class="fas fa-arrow-alt-square-right"></i>
        
        return (
          <button onClick={onClick} disabled={isEdge}>
            {pointer}
          </button>
        )
    }

    const api = axios.create({
        adapter: delayAdapterEnhancer(axios.defaults.adapter)
      });

      let cancelToken;

    useEffect(()=>{
        (async function()
        {
            try
            {
                if (typeof cancelToken != typeof undefined) cancelToken.cancel("Canceling previous req");
                cancelToken = axios.CancelToken.source();

                const res = await api.get(url, {cancelToken: cancelToken.token, delay: 4000});
                setVideos(res.data.promo);
                console.log(res.data);
                type === consts.PREV

            }
            catch(err)
            {
                console.log(err);
            }
        })()
    }, [url]);

    return (
        (videos) ?
        (
            <section className='container key-info-container videos-container'>
                <h2>Videos</h2>

                                <Carousel breakPoints={breakPoints} pagination={false} renderArrow={myArrow}>
                                {videos.map((video, i)=>
                                    <div key={i} className='video'>
                                        <ReactPlayer className='player' title={`video ${i}`} width="100%" height="100%" controls="true" muted="true" url={`${video.video_url}`}/>
                                    </div>)}
                                </Carousel>
 
                

            </section>
        ) : (null)

    )
}

export default Videos;
