import React, {useEffect, useState} from 'react';
import axios from "axios";
import delayAdapterEnhancer from 'axios-delay';
import ReviewContent from './ReviewContent';

const Review = ({id, episodeNum}) => {

    let url = `https://api.jikan.moe/v3/anime/${id}/reviews?q=&order_by=start_date&sort=desc&page=1`;

    const [reviews, setReviews] = useState([]);

    const api = axios.create({
        adapter: delayAdapterEnhancer(axios.defaults.adapter)
      });

      let cancelToken;

    useEffect(()=>{
        (async function()
        {
            try
            {
                if (typeof cancelToken != typeof undefined) cancelToken.cancel("Canceling previous req");
                cancelToken = axios.CancelToken.source();

                const res = await api.get(url, {cancelToken: cancelToken.token, delay: 10000});
                setReviews(res.data.reviews);
                console.log(res.data);
                res.data.reviews.map((review)=> console.log(review.reviewer.username + (700 + review.content.slice(700).indexOf(" ")) + " " + review.content.length))
            }
            catch(err)
            {
                console.log(err);
            }
        })();
    }, [url]);

    return (
        <section className="container key-info-container reviews-container">
            <h2 className='section-title'>User Reviews</h2>
            <div className='reviews-box'>
                {reviews.map((review, i)=>
                <div key={i} className='review'>
                    <div className="review-row">
                        <div className="user-profile">
                            <img src={review.reviewer.image_url} alt="Reviewer pic" />
                            <h3 className='username'>{review.reviewer.username}</h3>
                            <p>{[Date(review.date).toString().slice(4, 10), ", ", Date(review.date).toString().slice(11, 15)].join("")}</p>
                        </div>
                        <div className="score-and-seen">
                            <div className="score"><span><span id='score-big'><i className='fas fa-star'></i>{review.reviewer.scores.overall}</span> / 10</span></div>
                            <div className="seen">Episodes seen: {review.reviewer.episodes_seen} out of {episodeNum}</div>
                        </div>
                    </div>

                    <ReviewContent review={review}/>

                    <div className="review-helpful">
                        Helpful: {review.helpful_count}
                    </div>

                </div>)}
                
            </div>
        </section>

    )
}

export default Review;
