import React, {useState, useEffect} from 'react'
import axios from "axios";

const WatchStatus = ({id}) => {
    let url=`https://api.jikan.moe/v3/anime/${id}/stats`;

    const [watching, setWatching] = useState();
    const [watched, setWatched] = useState();
    const [dropped, setDropped] = useState();


    useEffect(() =>
    {
        (async function()
        {
            try{
                const res = await axios.get(url);
                setWatching(res.data.watching);
                setWatched(res.data.completed);
                setDropped(res.data.dropped);
                
            }
            catch(err)
            {
                console.log(err);
            }

        })();
    }, [url])


    return (
        <div className="watch-status">
            <div className="watched">People watched: {watched}</div>
            <div className="watching">People watching: {watching}</div>
            <div className="dropped">People dropped: {dropped}</div>
        </div>

    )
}

export default WatchStatus;;
