import React from 'react'
import { useParams } from 'react-router-dom'
import RatingSummary from './RatingSummary';
import ReviewList from './ReviewList'

const ReviewPage = () => {

    const {id} = useParams();

  return (
    <div className='review-page'>
        <RatingSummary id={id}/>
        <ReviewList id={id}/>
    </div>
  )
}

export default ReviewPage
