import axios from 'axios';
import delayAdapterEnhancer from 'axios-delay';
import React, { useEffect, useState } from 'react'
import ReactPlayer from "react-player/youtube";

const Videos = ({id}) => {

    let url = `https://api.jikan.moe/v3/anime/${id}/videos`;

    const [videos, setVideos] = useState([]);
    const [style, setStyle] = useState(0);
    const [clicks, setClicks] = useState(0);


    

    const slideRight = function()
    {
        
        if (clicks < videos.length - 3)
        {
            setStyle(style + 31.63);
            setClicks(clicks + 1);
            console.log(clicks);
        }
    }

    const slideLeft = function()
    {
        
        if (clicks > 0)
        {
            setStyle(style - 31.63);
            setClicks(clicks - 1);
            console.log(clicks);
        }
    }

    /*Touch slider*/

    let sliderContainer = document.querySelector('.video-slider-container');
    let slider = document.querySelector('.video-slider');

    let pressed = false;
    const [startX, setStartX] = useState();
    const [x, setX] = useState();


    const MouseDown = function(event)
    {
        pressed = true;
        setStartX(event.nativeEvent.offsetX - slider.offsetLeft);
        console.log(startX);
        sliderContainer.style.cursor = 'grabbing';
    }

    const MouseEnter = function(event)
    {
        sliderContainer.style.cursor = 'grab';
    }

    const MouseUp = function(event)
    {
        sliderContainer.style.cursor = 'grab';
        pressed = false;
    }

    const MouseMove = function(event)
    {
        if(!pressed) return;
        event.preventDefault();

        setX(event.nativeEvent.offsetX);
        console.log(x - startX);
        slider.style.transform = `translateX(${x - startX}px)`;
    }

    const api = axios.create({
        adapter: delayAdapterEnhancer(axios.defaults.adapter)
      });

      let cancelToken;

      

    useEffect(()=>{
        (async function()
        {
            try
            {
                if (typeof cancelToken != typeof undefined) cancelToken.cancel("Canceling previous req");
                cancelToken = axios.CancelToken.source();

                const res = await api.get(url, {cancelToken: cancelToken.token, delay: 4000});
                setVideos(res.data.promo);
                console.log(res.data);
            }
            catch(err)
            {
                console.log(err);
            }
        })()
    }, [url]);

    return (
        (videos) ?
        (
            <section className='container key-info-container videos-container'>
                <h2>Videos</h2>


                <div className="arrow-container">
                    <i class="fas fa-arrow-alt-square-left" onClick={slideLeft}></i>
                    <div className="video-slider-container" onMouseEnter={MouseEnter} onMouseDown={MouseDown} onMouseUp={MouseUp} onMouseMove={MouseMove}>
                        <div className="video-slider" style={{transform: 'translateX(-' + style + '%)'}}>
                            {videos.map((video, i)=>
                                <div key={i} className='video'>
                                    {/* <iframe title={`video ${i}`} width="420" height="315"
                                        src={video.video_url}>
                                    </iframe> */}
                                    <ReactPlayer className='player' title={`video ${i}`} width="100%" height="100%" controls="true" muted="true" url={`${video.video_url}`}/>
                                </div>)}
                        </div>
                    </div>
                    <i class="fas fa-arrow-alt-square-right" onClick={slideRight}></i>
                </div>



            </section>
        ) : (null)

    )
}

export default Videos;
