import React, {useState, useEffect} from 'react';
import axios from "axios";
import delayAdapterEnhancer from 'axios-delay';

const AnimeSearchPage = () => {

const [pageNum, setPageNum] = useState(1);
const [animeSearchList, setAnimeSearchList] = useState([<AnimeSearchList key={1} pageNum={1}/>])

  const url = `https://api.jikan.moe/v4/anime?q=Fate/Zero&page=${pageNum}`;

  const api = axios.create({
    adapter: delayAdapterEnhancer(axios.defaults.adapter)
  });

  let cancelToken;

  useEffect(() =>
  {
      (async function()
      {
          try
          {
              if (typeof cancelToken != typeof undefined) cancelToken.cancel("Canceling previous req");
              cancelToken = axios.CancelToken.source();
              const res = await api.get(url, {cancelToken: cancelToken.token, delay: 0});
              setAnimeSearchList(res.data.data);
              console.log(res.data);
          }
          catch(err)
          {
              console.log(err);
          }
      })();

  }, [url])

  return (
    <div>
        <div id="grid-container">
            <div className='container'>
                <div className='grid'>
                    {animeSearchList.map((anime, i)=>{return <Anime key={i} id={anime.mal_id} title={anime.title} image_url={anime.images.jpg.image_url}/>})}
                </div>
            </div>
        </div>
    </div>
  )
}

export default AnimeSearchPage
