import React, {useState, useEffect} from 'react';
import { useLocation, useParams } from 'react-router-dom';
import axios from "axios";
import delayAdapterEnhancer from 'axios-delay';
import AnimeSearchList from './AnimeSearchList';
import Navbar from './components/Navbar';

const AnimeSearchPage = () => {

const { search } = useParams();

const [pageNum, setPageNum] = useState(1);
const [animeSearchList, setAnimeSearchList] = useState([<AnimeSearchList key={1} pageNum={1} search={search}/>])

const [refresh, setRefresh] = useState(true);

const HandleLoadMore = (e) =>
{
  e.preventDefault();
  setAnimeSearchList([...animeSearchList, <AnimeSearchList key={animeSearchList.length + 1} pageNum={animeSearchList.length + 1} search={search}/>])

}

const api = axios.create({
    adapter: delayAdapterEnhancer(axios.defaults.adapter)
  });

  let cancelToken;

useEffect(()=>{
    (async function()
    {
        try
        {
            if (typeof cancelToken != typeof undefined) cancelToken.cancel("Canceling previous req");
            cancelToken = axios.CancelToken.source();

        }
        catch(err)
        {
            console.log(err);
        }
    })();
}, [refresh]);

  return (
    <div>
        <Navbar setRefresh={setRefresh} refresh={refresh}/>
        <div id="grid-container">
            <div className='container'>
                <div className='grid'>
                    {animeSearchList}
                </div>
            </div>
        </div>
        <div className="reviews-box">
            <div className="reviews-load-more" onClick={HandleLoadMore}><h2>Load More</h2></div>
        </div>
    </div>
  )
}

export default AnimeSearchPage
