import React, {useState, useEffect} from 'react';
import { Link } from 'react-router-dom';
import Logo from './Logo'
import { useNavigate } from 'react-router-dom';

const Navbar = () => {

  let navigate = useNavigate();

  const [search, setSearch] = useState();


  const HandleChange = (e) =>
  {
    setSearch(e.target.value);
  }

  const HandleSearch = (e) =>
  {
    e.preventDefault();
    navigate(`/search/${search}`);
  }

  
  return (
    <div className='navbar'>
        <Link to={'/'}><Logo/></Link>
        <div className="navbar-search-container">
          <input type="text" className="navbar-search" placeholder="Search for an anime..." onChange={HandleChange}/>
          <button className="navbar-search-btn" type="submit" onClick={HandleSearch}>
            <i className="fas fa-search"></i>

          </button>
        </div>

        <div className="navbar-search-container-mobile">
          <input type="text" className="navbar-search mobile" placeholder="Search for an anime..." onChange={HandleChange}/>
          <i className="fal fa-times mobile-search"></i>
        </div>


        <button className="navbar-search-btn mobile" type="submit" onClick={HandleSearch}>
            <i className="fas fa-search"></i>
        </button>

    </div>
  )
}

export default Navbar
