import axios from 'axios';
import React, {useEffect, useState} from 'react'
import { Link } from "react-router-dom"
import WatchStatus from './WatchStatus';

const KeyInfo = ({id}) => {
    let url = `https://api.jikan.moe/v3/anime/${id}`;

    const [score, setScore] = useState();
    const [title, setTitle] = useState("");
    const [pic, setPic] = useState();
    const [trailer, setTrailer] = useState();
    const [genres, setGenres] = useState([]);
    const [synopsis, setSynopsis] = useState("");




    useEffect(()=>{
        (async function()
        {
            try
            {
                const res = await axios.get(url);
                console.log(res.data);
                setScore(res.data.score);
                setTitle(res.data.title);
                setPic(res.data.image_url);
                setTrailer(res.data.trailer_url)
                setGenres(res.data.genres);
                setSynopsis(res.data.synopsis);

            }
            catch(err)
            {
                console.log(err);
            }
        })();
    }, [url]);

    useEffect(()=>{
        (async function()
        {
            try
            {
                synopsis.replace("[Written by MAL Rewrite]", "");

            }
            catch(err)
            {
                console.log(err);
            }
        })();
    }, [synopsis]);

    return (
        <div>
            <div className="title">{title}</div>
            <div className="ratins-status">
                <Link to={`/${id}/ratings`}><div className="score">Overall score: {score}</div></Link>
                <WatchStatus id={id}/>
            </div>
            <div className="pic-trailer">
            <img src={pic} alt="main-pic"></img>
                {(trailer) ?
                (
                    <iframe title="trailer" width="420" height="315"
                        src={trailer}>
                    </iframe>
                ):(null)}
            </div>
            <div className="genres">
                {genres.map((genre, i)=>{return <div key={i} className='genre'>{genre.name}</div>})}
            </div>
            <div className="synopsis">{synopsis}</div>


        </div>
    )
}

export default KeyInfo;
