import React, {useState, useEffect} from 'react';
import axios from "axios";
import Anime from './Anime';


const Home = () => {



    const [sortValue, setSortValue] = useState("score");
    const [buttonValue, setButtonValue] = useState("5");
    const [animes, setAnimes] = useState([]);
    const [search, setSearch] = useState("");

    const url = `https://api.jikan.moe/v3/search/anime?q=&genre=${buttonValue}&order_by=${sortValue}&sort=desc&page=1`;

    const HandleSearch = (event) =>
    {
        setSearch(event.target.value)
    }

    const HandleSort = (event) =>
    {
        setSortValue(event.target.value);
        console.log(event.target.value);
    }

    const HandleSubmit = (event) =>
    {
        setButtonValue(event.target.value);
        console.log(event.target.value);
    }

    const filterdAnime = animes.filter(anime=>{
        return anime.title.toLowerCase().includes(search.toLowerCase());
    })

    useEffect(() =>
    {
        (async function()
        {
            try
            {
                const res = await axios.get(url);
                setAnimes(res.data.results);
                console.log(res.data);
            }
            catch(err)
            {
                console.log(err);
            }
        })();

    }, [url])

    return (
        <div>
            <nav>
                <input type="text" className="search-box" placeholder='Search for an anime...' value={search} onChange={HandleSearch}/>
                <ul>
                    <li><button value="1" onSubmit={HandleSubmit}>Action</button></li>
                    <li><button value="2" onSubmit={HandleSubmit}>Adventure</button></li>
                    <li><button value="8" onSubmit={HandleSubmit}>Drama</button></li>
                    <li><button value="24" onSubmit={HandleSubmit}>Sci-fi</button></li>
                    <li><button value="4" onSubmit={HandleSubmit}>Comedy</button></li>
                    <li><button value="22" onSubmit={HandleSubmit}>Romance</button></li>
                    <li><button value="10" onSubmit={HandleSubmit}>Fantasy</button></li>
                    <li><button value="7" onSubmit={HandleSubmit}>Mystery</button></li>
                    <li><button value="27" onSubmit={HandleSubmit}>Shounen</button></li>
                    <li><button value="42" onSubmit={HandleSubmit}>Seinen</button></li>
                    <li><button value="18" onSubmit={HandleSubmit}>Mecha</button></li>
                </ul>
            </nav>
            
            
            <select id="sort" onChange={HandleSort}>
                <option value="score">Ranking</option>
                <option value="members">Popularity</option>
                <option value="start_date">Newest</option>
                <option value="end_date">Oldest</option>
            </select>

            <div className='anime-list'>
                {filterdAnime.map((anime, i)=>{return <Anime key={i} id={anime.mal_id} title={anime.title}/>})}
            </div>

        </div>

    )
}

export default Home;
