import React from 'react';
import {Link} from "react-router-dom"; 

const Anime = ({id, title, image_url}) => {

    return (
        <Link to={`/${id}`}>
                <div className="grid-item" style={{background: `linear-gradient( rgba(0, 0, 0, 0.25), rgba(0, 0, 0, 0.25) ), url(${image_url}) center no-repeat`}}>
                    <img src={image_url} alt="Main Poster" />
                </div>

        </Link>
    )
}

export default Anime;
