import React, {useState, useEffect} from 'react';
import axios from "axios";
import delayAdapterEnhancer from 'axios-delay';
import Anime from './Anime';


const Home = () => {

    const [sortValue, setSortValue] = useState("score");
    const [buttonValue, setButtonValue] = useState("1");
    const [animes, setAnimes] = useState([]);
    const [search, setSearch] = useState("");


    const url = `https://api.jikan.moe/v3/search/anime?q=&genre=${buttonValue}&order_by=${sortValue}&sort=desc&page=1`;

    const HandleSearch = (event) =>
    {
        setSearch(event.target.value)
    }

    const HandleSort = (event) =>
    {
        setSortValue(event.target.value);
        console.log(event.target.value);
    }

    const HandleSubmit = (event) =>
    {
        setButtonValue(event.target.value);
        console.log(event.target.value);
    }

    const menuOpen = document.querySelector(".fa-bars");
    const menuClose = document.querySelector(".fa-times");
    const body = document.querySelector("body");

    const AddClass = () =>
    {
        body.classList.add("open");
    }

    const RemoveClass = () =>
    {
        body.classList.remove("open");
    }

    const filterdAnime = animes.filter(anime=>{
        return anime.title.toLowerCase().includes(search.toLowerCase());
    })

    const api = axios.create({
        adapter: delayAdapterEnhancer(axios.defaults.adapter)
      });

    let cancelToken;

    useEffect(() =>
    {
        (async function()
        {
            try
            {
                if (typeof cancelToken != typeof undefined) cancelToken.cancel("Canceling previous req");
                cancelToken = axios.CancelToken.source();
                const res = await api.get(url, {cancelToken: cancelToken.token, delay: 0});
                setAnimes(res.data.results);
                console.log(res.data);

                menuOpen.removeEventListener("click", AddClass);
                menuOpen.removeEventListener("click", RemoveClass);

                menuOpen.addEventListener("click", AddClass);
                menuClose.addEventListener("click", RemoveClass);
            }
            catch(err)
            {
                console.log(err);
            }
        })();

    }, [url])

    return (
        <div>
            <header>
                <div className="container">

                    <input type="text" className="search-box" placeholder='Search for an anime...' value={search} onChange={HandleSearch}/>

                    <nav>

                        <div className="menu-toggle">

                            <i className= "fas fa-bars"></i>
                            <i className= "fas fa-times open"></i>
                        </div>

                        <ul className='nav-list'>
                            <li className='nav-item'><button value="1" onClick={HandleSubmit}>Action</button></li>
                            <li className='nav-item'><button value="2" onClick={HandleSubmit}>Adventure</button></li>
                            <li className='nav-item'><button value="8" onClick={HandleSubmit}>Drama</button></li>
                            <li className='nav-item'><button value="24" onClick={HandleSubmit}>Sci-fi</button></li>
                            <li className='nav-item'><button value="4" onClick={HandleSubmit}>Comedy</button></li>
                            <li className='nav-item'><button value="22" onClick={HandleSubmit}>Romance</button></li>
                            <li className='nav-item'><button value="10" onClick={HandleSubmit}>Fantasy</button></li>
                            <li className='nav-item'><button value="7" onClick={HandleSubmit}>Mystery</button></li>
                            <li className='nav-item'><button value="27" onClick={HandleSubmit}>Shounen</button></li>
                            <li className='nav-item'><button value="42" onClick={HandleSubmit}>Seinen</button></li>
                            <li className='nav-item'><button value="18" onClick={HandleSubmit}>Mecha</button></li>
                        </ul>
                    </nav>

                </div>
            </header>

            
            
            <select id="sort" onChange={HandleSort}>
                <option value="score">Ranking</option>
                <option value="members">Popularity</option>
                <option value="start_date">Newest</option>
                <option value="end_date">Oldest</option>
            </select>

            <div className='anime-list'>
                {filterdAnime.map((anime, i)=>{return <Anime key={i} id={anime.mal_id} title={anime.title}/>})}
            </div>

        </div>

    )
}

export default Home;
