import axios from 'axios';
import delayAdapterEnhancer from 'axios-delay';
import React, { useEffect, useState } from 'react'
import ReactPlayer from "react-player/youtube";

const Videos = ({id}) => {

    let url = `https://api.jikan.moe/v3/anime/${id}/videos`;

    const [videos, setVideos] = useState([]);


    const api = axios.create({
        adapter: delayAdapterEnhancer(axios.defaults.adapter)
      });

      let cancelToken;

    useEffect(()=>{
        (async function()
        {
            try
            {
                if (typeof cancelToken != typeof undefined) cancelToken.cancel("Canceling previous req");
                cancelToken = axios.CancelToken.source();

                const res = await api.get(url, {cancelToken: cancelToken.token, delay: 4000});
                setVideos(res.data.promo);
                console.log(res.data);
            }
            catch(err)
            {
                console.log(err);
            }
        })()
    }, [url]);

    return (
        (videos) ?
        (
            <div className='videos-container'>
                {videos.map((video, i)=>
                    <div key={i} className='video'>
                        {/* <iframe title={`video ${i}`} width="420" height="315"
                            src={video.video_url}>
                        </iframe> */}
                        <ReactPlayer url={`${video.video_url}`}/>
                    </div>)}
            </div>
        ) : (null)

    )
}

export default Videos;
