import axios from 'axios';
import delayAdapterEnhancer from 'axios-delay';
import React, {useEffect, useState} from 'react'
import { Link } from "react-router-dom"
import WatchStatus from './WatchStatus';
import ReactPlayer from 'react-player/youtube';

const KeyInfo = ({id}) => {
    let url = `https://api.jikan.moe/v3/anime/${id}`;

    const [score, setScore] = useState();
    const [scoredBy, setScoredBy] = useState();
    const [title, setTitle] = useState("");
    const [pic, setPic] = useState();
    const [trailer, setTrailer] = useState();
    const [genres, setGenres] = useState([]);
    const [synopsis, setSynopsis] = useState("");
    const [type, setType] = useState("");
    const [episodes, setEpisodes] = useState();
    const [status, setStatus] = useState("");
    const [aired, setAired] = useState("");
    const [broadcast, setBroadcast] = useState("");
    const [duration, setDuration] = useState("");
    const [source, setSource] = useState("");
    const [rating, setRating] = useState("");
    const [producers, setProducers] = useState([]);
    const [licensors, setLicensors] = useState([]);
    const [studios, setStudios] = useState([]);


    const api = axios.create({
        adapter: delayAdapterEnhancer(axios.defaults.adapter)
      });

      let cancelToken;

    useEffect(()=>{
        (async function()
        {
            try
            {
                if (typeof cancelToken != typeof undefined) cancelToken.cancel("Canceling previous req");
                cancelToken = axios.CancelToken.source();
                const res = await api.get(url, {cancelToken: cancelToken.token, delay: 0});
                console.log(res.data);
                setScore(res.data.score);
                setScoredBy(res.data.scored_by);
                setTitle(res.data.title);
                setPic(res.data.image_url);
                setTrailer(res.data.trailer_url)
                setGenres(res.data.genres);
                setSynopsis(res.data.synopsis.replace(" [Written by MAL Rewrite]", ""));
                setType(res.data.type);
                setEpisodes(res.data.episodes);
                setStatus(res.data.status);
                setAired(res.data.aired.string);
                setBroadcast(res.data.broadcast);
                setDuration(res.data.duration);
                setSource(res.data.source);
                setRating(res.data.rating);
                setProducers(res.data.producers);
                setLicensors(res.data.licensors);
                setStudios(res.data.studios);


            }
            catch(err)
            {
                console.log(err);
            }
        })();
    }, [url]);


    return (
        <div>
            <div className="container">
                <div className="title-bar">
                    <div className="title"><h1>{title}</h1></div>
                    <div className="ratings-status">
                        <Link to={`/${id}/ratings`}><div className="score">Score: <span><span>{score}</span>/10</span><span>Scored by: {scoredBy}</span></div></Link>
                        <WatchStatus id={id}/>
                    </div>
                </div>

                <div className="pic-trailer">
                    <img src={pic} alt="main-pic"></img>
                    {(trailer) ?
                    (
                        // <iframe title="trailer" width="420" height="315"
                        //     src={trailer}>
                        // </iframe>
                        <div className="video-container-main">
                            <div className="video">
                                <ReactPlayer className='player' title="trailer" width="100%" height="100%" controls="true" muted="true" url={trailer}/>

                            </div>
                        </div>

                    ):(null)}
                </div>
                <div className="genres">
                    {genres.map((genre, i)=>{return <div key={i} className='genre'>{genre.name}</div>})}
                </div>
                <div className="synopsis">{synopsis}</div>
                <ul className="info">
                    <li>{`Type: ${type}`}</li>
                    <li>{`Episodes: ${episodes}`}</li>
                    <li>{`Status: ${status}`}</li>
                    <li>{`Aired: ${aired}`}</li>
                    <li>{`Broadcast: ${broadcast}`}</li>
                    <li>{`Duration: ${duration}`}</li>
                    <li>{`Source: ${source}`}</li>
                    <li>{`Rating: ${rating}`}</li>
                    <li>Producers: {producers.map((producer, i)=>{return <span key={i}>{producer.name}</span>}).reduce((prev, curr) => prev === null ? [curr] : [prev, ", ", curr], null)}</li>
                    <li>Licensors: {licensors.map((licensor, i)=>{return <span key={i}>{licensor.name}</span>}).reduce((prev, curr) => prev === null ? [curr] : [prev, ", ", curr], null)}</li>
                    <li>Studio(s): {studios.map((studio, i)=>{return <span key={i}>{studio.name}</span>}).reduce((prev, curr) => prev === null ? [curr] : [prev, ",", curr], null)}</li>
                </ul>
            </div>



        </div>
    )
}

export default KeyInfo;
