import React, {useEffect, useState} from 'react';
import { useParams } from 'react-router';
import axios from "axios";
import delayAdapterEnhancer from 'axios-delay';
import ReviewContent from './ReviewContent';
import { Link } from 'react-router-dom';

const ReviewPage = () => {

    const { id } = useParams();

    let url = `https://api.jikan.moe/v3/anime/${id}/reviews?q=&order_by=start_date&sort=desc&page=1`;

    const [reviews, setReviews] = useState([]);

    const api = axios.create({
        adapter: delayAdapterEnhancer(axios.defaults.adapter)
      });

      let cancelToken;

    useEffect(()=>{
        (async function()
        {
            try
            {
                if (typeof cancelToken != typeof undefined) cancelToken.cancel("Canceling previous req");
                cancelToken = axios.CancelToken.source();

                const res = await api.get(url, {cancelToken: cancelToken.token, delay: 8000});
                setReviews(res.data.reviews.slice(0, 3));
                console.log(res.data.reviews);
            }
            catch(err)
            {
                console.log(err);
            }
        })();
    }, [url]);

  return (
    <div className="container key-info-container">
        <h2 className='section-title'>User Reviews</h2>

    </div>
  )
}

export default ReviewPage
