import React, {useEffect, useState} from 'react';
import axios from "axios";
import delayAdapterEnhancer from 'axios-delay';

const Review = ({id}) => {

    let url = `https://api.jikan.moe/v3/anime/${id}/reviews?q=&order_by=start_date&sort=desc&page=1`;

    const [reviews, setReviews] = useState([]);
    const [dates, setDates] = useState([]);


    const api = axios.create({
        adapter: delayAdapterEnhancer(axios.defaults.adapter)
      });

      let cancelToken;

    useEffect(()=>{
        (async function()
        {
            try
            {
                if (typeof cancelToken != typeof undefined) cancelToken.cancel("Canceling previous req");
                cancelToken = axios.CancelToken.source();

                const res = await api.get(url, {cancelToken: cancelToken.token, delay: 10000});
                setReviews(res.data.reviews);
                if (res.data) setDates(res.data.review.map((i)=>Date(i.date)));
                console.log(res.data);
            }
            catch(err)
            {
                console.log(err);
            }
        })();
    }, [url]);

    return (
        <section className="container key-info-container reviews-container">
            <h2 className='section-title'>User Reviews</h2>
            <div className='reviews-box'>
                {reviews.map((review, i)=>
                <div key={i} className='review'>
                    <div className="review-row">
                        <img src={review.reviewer.image_url} alt="Reviewer pic" />
                        <h3>{`${review.reviewer.username} | ${Date(review.date).toString()}`}</h3>
                    </div>
                    

                </div>)}
                
            </div>
        </section>

    )
}

export default Review;
