import React, {useState, useEffect} from 'react';
import axios from "axios";
import delayAdapterEnhancer from 'axios-delay';
import Anime from './Anime';


const Home = () => {



    const [sortValue, setSortValue] = useState("score");
    const [buttonValue, setButtonValue] = useState("1");
    const [animes, setAnimes] = useState([]);
    const [search, setSearch] = useState("");
    const [toggle, setToggle] = useState(false);


    const url = `https://api.jikan.moe/v3/search/anime?q=&genre=${buttonValue}&order_by=${sortValue}&sort=desc&page=1`;

    const HandleSearch = (event) =>
    {
        setSearch(event.target.value)
    }

    const HandleSort = (event) =>
    {
        setSortValue(event.target.value);
        console.log(event.target.value);
    }

    const HandleSubmit = (event) =>
    {
        setButtonValue(event.target.value);
        console.log(event.target.value);
        setToggle(!toggle);
    }

    const HandleToggle = (event) =>
    {
        setToggle(!toggle);
    }

    const filterdAnime = animes.filter(anime=>{
        return anime.title.toLowerCase().includes(search.toLowerCase());
    })

    const api = axios.create({
        adapter: delayAdapterEnhancer(axios.defaults.adapter)
      });

    let cancelToken;

    useEffect(() =>
    {
        (async function()
        {
            try
            {
                if (typeof cancelToken != typeof undefined) cancelToken.cancel("Canceling previous req");
                cancelToken = axios.CancelToken.source();
                const res = await api.get(url, {cancelToken: cancelToken.token, delay: 0});
                setAnimes(res.data.results);
                console.log(res.data);
            }
            catch(err)
            {
                console.log(err);
            }
        })();

    }, [url])

    return (
        <div>
            <header>
                <div className={toggle ? "container open" : "container"}>

                    <nav>

                        <div className="menu-toggle">

                            <i onClick={HandleToggle} className="fas fa-bars"></i>
                            <i onClick={HandleToggle} className="fas fa-times"></i>
                        </div>

                        <ul className='nav-list'>
                            <li className='nav-item' value="0" onClick={HandleSubmit}>All</li>
                            <li className='nav-item' value="1" onClick={HandleSubmit}>Action</li>
                            <li className='nav-item' value="2" onClick={HandleSubmit}>Adventure</li>
                            <li className='nav-item' value="8" onClick={HandleSubmit}>Drama</li>
                            <li className='nav-item' value="24" onClick={HandleSubmit}>Sci-fi</li>
                            <li className='nav-item' value="4" onClick={HandleSubmit}>Comedy</li>
                            <li className='nav-item' value="22" onClick={HandleSubmit}>Romance</li>
                            <li className='nav-item' value="10" onClick={HandleSubmit}>Fantasy</li>
                            <li className='nav-item' value="7" onClick={HandleSubmit}>Mystery</li>
                            <li className='nav-item' value="27" onClick={HandleSubmit}>Shounen</li>
                            <li className='nav-item' value="42" onClick={HandleSubmit}>Seinen</li>
                            <li className='nav-item' value="18" onClick={HandleSubmit}>Mecha</li>
                        </ul>
                    </nav>

                </div>
            </header>

            <div className="search-container">
                <div className="search-box">
                    <input className="search-text" type="text" placeholder='Search for an anime...' value={search} onChange={HandleSearch}/><a href="#" className="search-btn"><i className="fas fa-search"></i></a>
                </div>
            </div>

            <div className="container">
                <select id="sort" onChange={HandleSort}>
                    <option value="score">Ranking</option>
                    <option value="members">Popularity</option>
                    <option value="start_date">Newest</option>
                    <option value="end_date">Oldest</option>
                </select>
            </div>


            <div className="container">
                <div className='grid'>
                    {filterdAnime.map((anime, i)=>{return <Anime key={i} id={anime.mal_id} title={anime.title} image_url={anime.image_url}/>})}
                </div>
            </div>


        </div>

    )
}

export default Home;
