import React, {useState} from 'react'
import { Slice } from './helpers/helpers';

const ReviewContent = ({review}) => {

    const [toggleReview, setToggleReview] = useState(true);

    const handleToggleReview = function()
    {
        setToggleReview(!toggleReview);
    }

    return (
      <div className='review-content'>
          {toggleReview ? <>{Slice(review.content, 700)} <span className="read-more" onClick={handleToggleReview}>Read More</span></> : <>{review.content} <span className="read-more" onClick={handleToggleReview}>Read Less</span></>}     
      </div>
    )
}

export default ReviewContent
