import React, {useState, useEffect} from 'react';
import { useParams } from 'react-router-dom';
import AnimeSearchList from './AnimeSearchList';
import Navbar from './components/Navbar';

const AnimeSearchPage = () => {

const { search } = useParams();

const [animeSearchList, setAnimeSearchList] = useState([<AnimeSearchList key={1} pageNum={1} search={search}/>])


const HandleLoadMore = (e) =>
{
  e.preventDefault();
  setAnimeSearchList([...animeSearchList, <AnimeSearchList key={animeSearchList.length + 1} pageNum={animeSearchList.length + 1} search={search}/>])

}


  return (
    <div>
        <Navbar/>
        <div id="grid-container">
            <div className='container'>
                <div className='grid'>
                    {animeSearchList}
                </div>
            </div>
        </div>
        {animeSearchList[animeSearchList.length - 1].has_next_page === true ? <div className="reviews-box">
            <div className="reviews-load-more" onClick={HandleLoadMore}><h2>Load More</h2></div>
        </div> : null}
    </div>
  )
}

export default AnimeSearchPage
