import React, {useState, useEffect} from 'react'
import { Slice } from './helpers/helpers';

const ReviewContent = ({review, id}) => {

    const [toggleReview, setToggleReview] = useState(true);

    const handleToggleReview = function()
    {
        setToggleReview(!toggleReview);
    }

    let url = `https://api.jikan.moe/v3/anime/${id}/reviews?q=&order_by=start_date&sort=desc&page=1`;


    useEffect(()=>{
        (async function()
        {
            try
            {
                if (typeof cancelToken != typeof undefined) cancelToken.cancel("Canceling previous req");
                cancelToken = axios.CancelToken.source();
                const res = await api.get(url, {cancelToken: cancelToken.token, delay: 0});

                console.log(res.data);
            }
            catch(err)
            {
                console.log(err);
            }
        })();
    }, [url]);

    return (
      <div className='review-content'>
          {review.content.length > 700 + review.content.slice(700).indexOf(" ") ? (toggleReview ? <>{Slice(review.content, 700)} <span className="read-more" onClick={handleToggleReview}>Read More</span></> : <>{review.content} <span className="read-more" onClick={handleToggleReview}>Read Less</span></>) : <>{review.content}</>}     
      </div>
    )
}

export default ReviewContent
