import React, {useState, useEffect} from 'react'
import axios from "axios";

const Rating = ({id}) => {
    let url=`https://api.jikan.moe/v3/anime/${id}/stats`;

    const [ratings, setRatings] = useState([]);
    const [score, setScore] = useState(0);
    const [watching, setWatching] = useState(0);
    const [watched, setWatched] = useState(0);
    const [dropped, setDropped] = useState(0);


    useEffect(() =>
    {
        (async function()
        {

            try{
                const res = await axios.get(url);
                setWatching(res.data.watching);
                setRatings(Object.values(res.data.scores)); //Converting object into array so that 'map' function can be applied
                console.log(res.data);

                let mult;
                let sum;
                mult = 0;
                sum = 0;
                Object.values(res.data.scores).forEach((rating, i)=>{mult += (rating.votes*(i+1)); sum += rating.votes;});
                setScore(mult/sum);

                
            }
            catch(err)
            {
                console.log(err);
            }

        })();
    }, [url])

    // useEffect(() =>
    // {
