import React, {useEffect, useState} from 'react';
import axios from "axios";
import delayAdapterEnhancer from 'axios-delay';

const Cast = ({id}) => {

    let url = `https://api.jikan.moe/v3/anime/${id}/characters_staff`;

    const [cast, setCast] = useState([]);

    const api = axios.create({
        adapter: delayAdapterEnhancer(axios.defaults.adapter)
      });

    useEffect(()=>{
        (async function()
        {
            try
            {
                if (typeof cancelToken != typeof undefined) cancelToken.cancel("Canceling previous req");
                cancelToken = axios.CancelToken.source()
                const res = await api.get(url, {cancelToken: cancelToken.token, delay: 0});
                setCast(res.data.characters);
                console.log(res.data);
            }
            catch(err)
            {
                console.log(err);
            }
        })();
    }, [url]);

    return (
        <div className='cast'>
            {cast.map((character, i)=>
            <div key={i} className='character-row'>
                {<div className='character-name'><strong>{character.name}</strong></div>}
                {<div className='character-pic'><img src={character.image_url} alt="character-pic"></img></div>}
                {character.voice_actors.map((actor, i)=>actor.language.includes("Japanese") ? <><div key={i} className='actor-name'>Japanese: {actor.name}</div><div className='actor-pic'><img src={actor.image_url} alt="actor-pic"></img></div></> : (null))}
                {character.voice_actors.map((actor, i)=>actor.language.includes("English") ? <div key={i} className='actor-name'>English: {actor.name}</div> : (null))}
            </div>)}
            
        </div>
    )
}

export default Cast;
