import React, {useState} from 'react';
import { useEffect } from 'react';
import { Slice } from './helpers/helpers';

const ReviewContent = ({review, id}) => {

    const [toggleReview, setToggleReview] = useState(true);
    const [width, setWidth] = useState(window.innerWidth);

    const handleToggleReview = function()
    {
        setToggleReview(!toggleReview);
    }

    const HandleResize = () => setWidth(window.innerWidth);

    useEffect(() =>
    {
        (async function()
        {
            try
            {
                window.addEventListener('resize', HandleResize);

            }
            catch(err)
            {
                console.log(err);
            }
        })();
  
    }, [])

    return (
      <div className='review-content'>
          {review.review.length > 700 + review.review.slice(700).indexOf(" ") ? (toggleReview ? <>{Slice(review.review, 700)} <span className="read-more" onClick={handleToggleReview}>Read More</span></> : <>{review.review} <span className="read-more" onClick={handleToggleReview}>Read Less</span></>) : <>{review.review}</>}     
          <p>{width}</p>
      </div>
    )
}

export default ReviewContent
