import React, {useState, useEffect} from 'react';
import axios from "axios";
import delayAdapterEnhancer from 'axios-delay';
import Anime from './Anime';
import {Link} from "react-router-dom";


const Home = () => {



    const [sortValue, setSortValue] = useState("score");
    const [buttonValue, setButtonValue] = useState("1");
    const [animes, setAnimes] = useState([]);
    const [search, setSearch] = useState("");

    const url = `https://api.jikan.moe/v3/search/anime?q=&genre=${buttonValue}&order_by=${sortValue}&sort=desc&page=1`;

    const HandleSearch = (event) =>
    {
        setSearch(event.target.value)
    }

    const HandleSort = (event) =>
    {
        setSortValue(event.target.value);
        console.log(event.target.value);
    }

    const HandleSubmit = (event) =>
    {
        setButtonValue(event.target.value);
        console.log(event.target.value);
    }

    const filterdAnime = animes.filter(anime=>{
        return anime.title.toLowerCase().includes(search.toLowerCase());
    })

    const api = axios.create({
        adapter: delayAdapterEnhancer(axios.defaults.adapter)
      });

    let cancelToken;

    useEffect(() =>
    {
        (async function()
        {
            try
            {
                if (typeof cancelToken != typeof undefined) cancelToken.cancel("Canceling previous req");
                cancelToken = axios.CancelToken.source();
                const res = await axios.get(url, {cancelToken: cancelToken.token, delay: 0});
                setAnimes(res.data.results);
                console.log(res.data);
            }
            catch(err)
            {
                console.log(err);
            }
        })();

    }, [url])

    return (
        <div>
            <nav>
                <input type="text" className="search-box" placeholder='Search for an anime...' value={search} onChange={HandleSearch}/>
                <ul>
                    <li><NavLink value="1" onClick={HandleSubmit}>Action</NavLink></li>
                    <li><NavLink value="2" onClick={HandleSubmit}>Adventure</NavLink></li>
                    <li><NavLink value="8" onClick={HandleSubmit}>Drama</NavLink></li>
                    <li><NavLink value="24" onClick={HandleSubmit}>Sci-fi</NavLink></li>
                    <li><NavLink value="4" onClick={HandleSubmit}>Comedy</NavLink></li>
                    <li><NavLink value="22" onClick={HandleSubmit}>Romance</NavLink></li>
                    <li><NavLink value="10" onClick={HandleSubmit}>Fantasy</NavLink></li>
                    <li><NavLink value="7" onClick={HandleSubmit}>Mystery</NavLink></li>
                    <li><NavLink value="27" onClick={HandleSubmit}>Shounen</NavLink></li>
                    <li><NavLink value="42" onClick={HandleSubmit}>Seinen</NavLink></li>
                    <li><NavLink value="18" onClick={HandleSubmit}>Mecha</NavLink></li>
                </ul>
            </nav>
            
            
            <select id="sort" onChange={HandleSort}>
                <option value="score">Ranking</option>
                <option value="members">Popularity</option>
                <option value="start_date">Newest</option>
                <option value="end_date">Oldest</option>
            </select>

            <div className='anime-list'>
                {filterdAnime.map((anime, i)=>{return <Anime key={i} id={anime.mal_id} title={anime.title}/>})}
            </div>

        </div>

    )
}

export default Home;
