import React, {useState, useEffect} from 'react'
import axios from "axios";

const Rating = ({id}) => {
    let url=`https://api.jikan.moe/v3/anime/${id}/stats`;

    const [ratings, setRatings] = useState([]);
    const [scores, setScores] = useState(0);

    useEffect(() =>
    {
        (async function()
        {

            try{
                const res = await axios.get(url);
                setRatings(score); //Converting object into array so that 'map' function can be applied
                console.log(res.data);

                
            }
            catch(err)
            {
                console.log(err);
            }

        })();
    }, [url])

    useEffect(() =>
    {
        (async function()
        {
            let mult;
            var sum;
            try{
                    mult = 0;
                    sum = 0;
                    ratings.forEach((rating, i)=>{mult += (rating.votes*(i+1)); sum += rating.votes;});
                    setScores(mult/sum);
            }
            catch(err)
            {
                console.log(err);
            }

        })();
    }, [ratings])





    return (
        <div>
            <div>
                {scores}
            </div>
            <div className="rating-list">
                {ratings.map((rating, i)=>{return <div key={i} className="rating">
                    <div className="rating-number">{i+1}</div>
                    <div className="rating-percentage">{rating.percentage} %</div>
                </div>})}
            </div>
        </div>

    )
}

export default Rating;
